import React from "react";
import { Media, Player } from "react-media-player";
import { MuteUnmute, PlayPause } from "react-media-player/lib/controls";
import ReactPlayer from "react-player";

const Popup = (props, { setIsVideo, isVideo }) => {
  console.log(isVideo);

  return (
    <>
      {isVideo && (
        <div className="popup fixed z-10 w-full h-full bg-slate-800/80 flex p-10">
          <div className="popup-inner w-full">
            {props.children}
            <button className="close-btn" onClick={() => setIsVideo(false)}>
              Close
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default Popup;
