import React, { useEffect, useState } from "react";
import Navbar from "../Navbar/Navbar";
import fire from "../../images/fire.gif";
import guy from "../../images/guy.png";
import { Reveal2, Reveal3, Reveal5, RevealPic } from "../utils/Reveal";

const BannerIntro = () => {
  const [isLoading, setIsLoading] = useState(true);

  const isLoadingHandler = () => {
    setTimeout(() => {
      setIsLoading(false);
    }, 5000);
  };

  useEffect(() => {
    isLoadingHandler();

    console.log(isLoading);
  }, []);

  return (
    <>
      <div id="home" className={`${isLoading ? 'bg-black' : 'bg-intro-Image'} bg-cover h-screen pt-20 md:pt-0 ease-linear duration-200`}>
      <div className="absolute w-screen h-screen animate-introduction " ></div>
        <div className="grid md:grid-cols-2 w-screen h-5/6 md:h-full min-h-lg text-white">
          <div className="flex-1 self-center justify-center text-center md:text-left m-auto ">
            <Reveal2>
              <h1 className=" text-5xl md:text-6xl">Hi, I'm Hanson</h1>
            </Reveal2>
            <Reveal3>
              <h1 className=" text-md px-6 md:px-0 md:text-md pt-3 pb-6 max-w-sm">
                I build and design industry standard web applications.
              </h1>
            </Reveal3>
            <Reveal5>
              <div className="m-auto md:m-0 grid md:grid-cols-2 gap-4 max-w-xs">
                <a
                  href="#about"
                  className="p-3 px-8 rounded-xl border border-purple-600 bg-purple-600 hover:bg-purple-500 ease-in duration-100"
                >
                  <button className="font-mono ">ABOUT ME</button>
                </a>
                <a
                  href="#portfolio"
                  className="p-3 px-8 rounded-xl border  hover:bg-slate-700 ease-in duration-100"
                >
                  <button type="button" className=" font-mono">
                    PORTFOLIO
                  </button>
                </a>
              </div>
            </Reveal5>
          </div>
          <div className=" flex self-end justify-center mb-10 md:mb-20 min-w-20 mt-5 md:mt-0">
            <div className="flex items-end ">
              <img src={fire} alt="" className="h-40  " />
              <img src={guy} alt="" className=" h-60 " />
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default BannerIntro;
