import React, { useContext, useEffect, useState } from "react";
import { RevealBox } from "../utils/RevealPort";
import githubBig from "../../images/githubBig.png";
import laptop from "../../images/laptop.png";
import Popup from "./Popup";
import { userContext } from "../../Context/userContext";

const Project = ({ post }) => {
  const { setIsVideo, loadVideoHandler } = useContext(userContext);

  const [hover, setHover] = useState(false);
  const [isDesktop, setDesktop] = useState(false);

  useEffect(() => {
    if (window.innerWidth > 544) {
      setDesktop(true);
    } else {
      setDesktop(false);
    }

    const updateMedia = () => {
      if (window.innerWidth > 544) {
        setDesktop(true);
      } else {
        setDesktop(false);
      }
    };
    window.addEventListener("resize", updateMedia);
    return () => window.removeEventListener("resize", updateMedia);
  }, []);

  const handleMouseEnter = () => {
    setHover(true);
  };
  const handleMouseLeave = () => {
    setHover(false);
  };

  console.log(post.private);

  return (
    <div
      href="##"
      className=" ease-in duration-100 "
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <RevealBox>
        <div className=" backdrop-blur-lg flex flex-col">
          {hover && isDesktop && (
            <div className=" absolute flex w-full h-full justify-center items-center transition ease-in duration-75 hover:bg-slate-100/60 rounded-lg">
              <a href={post.github} rel="noreferrer" target="_blank">
                <img
                  src={githubBig}
                  alt=""
                  className="transition ease-in duration-75 m-3 h-28 hover:scale-105 "
                />
              </a>
              <button onClick={() => loadVideoHandler(post.url)}>
                <img
                  src={laptop}
                  alt=""
                  className=" transition ease-in duration-75 m-3 h-28 hover:scale-105 "
                />
              </button>
            </div>
          )}

          <img
            src={post.image}
            alt=""
            style={{
              width: "auto",
              height: "15rem",
              objectFit: "cover",
              borderRadius: "10px 10px 0 0",
            }}
          />
          {/* ADD HOVER ELEMENT HERE */}

          <div
            className="p-3  bg-slate-700 rounded-b-lg -z-1"
            style={{ minHeight: "6rem" }}
          >
            <h3 className="text-xl "> {post.name} </h3>
            <p
              className={`text-sm transition-all ease-in duration-150 mb-3

              `}
            >
              {post.desc}
            </p>
            <div className="flex flex-row transition-all duration-150 w-fit overflow-hidden">
              {isDesktop &&
                post.skills.map((skill) => (
                  <img src={skill} alt="" className="w-fit h-6" />
                ))}
            </div>
          </div>
        </div>
      </RevealBox>
    </div>
  );
};

export default Project;
