import React from "react";
import { useState } from "react";
import { RevealBox } from "../utils/RevealPort";

const Project = ({ post }) => {
  const [hover, setHover] = useState(false);

  const handleMouseEnter = () => {
    setHover(true);
  };
  const handleMouseLeave = () => {
    setHover(false);
  };

  return (
    <a
      href="##"
      className=" ease-in duration-100 "
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <RevealBox>
        <div className="w-full backdrop-blur-lg rounded-lg flex flex-col">
          <div className="w-auto object-cover h-56">
            <img src={post.image} alt="" classname="w-96 h-60 rounded-t-lg " />
            {/* ADD HOVER ELEMENT HERE */}
          </div>
          <div className="p-3  bg-slate-400">
            <h3 className="text-xl "> {post.name} </h3>
            <p
              className={`text-sm transition-all h-10 ${
                hover ? "hidden" : "block"
              } ease-in duration-150 `}
            >
              {post.desc}
            </p>
            <div className="flex flex-row transition-all duration-150 w-fit overflow-hidden">
              {hover &&
                post.skills.map((skill) => (
                  <img src={skill} alt="" className="w-fit h-10" />
                ))}
            </div>
          </div>
        </div>
      </RevealBox>
    </a>
  );
};

export default Project;
