import React, { useContext } from "react";
import { Media, Player } from "react-media-player";
import { MuteUnmute, PlayPause } from "react-media-player/lib/controls";
import ReactPlayer from "react-player";
import { userContext } from "../../Context/userContext";
import HighlightOffIcon from "@mui/icons-material/HighlightOff";

const Popup = () => {
  const { isVideo, setIsVideo, URL } = useContext(userContext);
  console.log(URL);

  return (
    <>
      {isVideo && (
        <div
          className="transition duration-150 popup fixed z-10 w-full h-full bg-slate-800/80 flex p-10"
          onClick={() => setIsVideo(false)}
        >
          <div className="popup-inner w-full">
            <ReactPlayer url={URL} width="100%" height="90%" />
            <button
              className="close-btn w-full"
              onClick={() => setIsVideo(false)}
            >
              <HighlightOffIcon />
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default Popup;
