import React, { useContext, useState } from "react";
import { userContext } from "../../Context/userContext";
import MenuIcon from "@mui/icons-material/Menu";

const Navbar = () => {
  const { isDesktop } = useContext(userContext);
  const [isNavbarOpened, setIsNavbarOpened] = useState(false);

  if (isDesktop) {
    return (
      <>
        <div class=" w-screen flex justify-end pe-10 sticky top-0 h-0 z-9 uppercase font-mono">
          <a href="#home" class="p-5 text-md text-white">
            Home
          </a>
          <a href="#about" class="p-5 text-md text-white">
            About
          </a>
          <a href="#portfolio" class="p-5 text-md text-white">
            Portfolio
          </a>
          <a href="#design" class="p-5 text-md text-white">
            Design
          </a>
          <a href="#contact" class="p-5 text-md text-white">
            Contact
          </a>
        </div>
      </>
    );
  } else {
    return (
      <div class=" w-screen sticky h-0 z-9 top-0 z-20 ">
        {isNavbarOpened && <div>hello</div>}
        <div className="h-screen " onClick={() => setIsNavbarOpened(false)}>
          <div
            href=""
            className=" flex flex-col w-full uppercase font-mono backdrop-blur-md shadow-lg z-21 "
          >
            <div class="p-7 flex text-md text-white justify-between">
              <a href="#home">Home</a>
              <MenuIcon />
            </div>
            <a href="#about" class="p-7 text-md text-white">
              About
            </a>
            <a href="#portfolio" class="p-7 text-md text-white">
              Portfolio
            </a>
            <a href="#design" class="p-7 text-md text-white">
              Design
            </a>
            <a href="#contact" class="p-7 text-md text-white">
              Contact
            </a>
          </div>
        </div>
      </div>
    );
  }
};

export default Navbar;
