import React from "react";
import Navbar from "../Navbar/Navbar";
import fire from '../../images/fire.gif'

const BannerIntro = () => {
  return (
    <div className="bg-intro-Image bg-cover h-screen">
      <Navbar className="" />
      <div className="grid grid-cols-2 w-screen h-screen text-white  ">
        <div className="flex-1 self-center justify-center ml-40 ">
          <h1 className="text-6xl">Hi, I'm Hanson</h1>
          <h1 className="text-md pt-3 pb-6 max-w-sm">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
          </h1>
          <div className="grid grid-cols-2 gap-4 max-w-xs" >
          <button className="p-3 px-8 rounded-xl border border-purple-600 bg-purple-600 hover:bg-purple-500 ease-in duration-100 font-mono " >ABOUT ME</button>
          <button className="p-3 px-8 rounded-xl border  hover:bg-slate-800 ease-in duration-100 font-mono " >PORTFOLIO</button>
          </div>
        </div>
        <div className="flex-1">Bonfire guy
          <img src={fire} alt="" className="w-10"/>
        </div>
      </div>
    </div>
  );
};

export default BannerIntro;
