import React from "react";
import linkedin from "../../images/linkedin.png";
import github from "../../images/github.png";
import email from "../../images/email.png";
import { Reveal3, Reveal4, Reveal5 } from "../utils/Reveal";
import { RevealTitle } from "../utils/RevealPort";

const Contact = () => {
  return (
    <div className="grid items-center justify-center text-center  p-20 pb-40">
      <RevealTitle>
        <h1>Hey I know you have that question or comment for me.</h1>
        <h1 className="text-3xl">Get in Touch with Me Today</h1>
      </RevealTitle>
      <div className="flex pt-7 gap-2 items-center justify-center">
        <a
          href="https://www.linkedin.com/in/hanson-liao-2031/"
          target="_blank"
          rel="noopener noreferrer"
        >
          <Reveal3>
            <img
              src={linkedin}
              alt=""
              className="absolute w-16 linear  hover:animate-ping"
            />
            <img src={linkedin} alt="" className="w-16 " />
          </Reveal3>
        </a>
        <a
          href="https://github.com/hansonliao98"
          target="_blank"
          rel="noopener noreferrer"
        >
          <Reveal4>
            <img
              src={github}
              alt=""
              className="absolute w-16 linear  hover:animate-ping"
            />
            <img src={github} alt="" className="w-16 " />
          </Reveal4>
        </a>
        <a
          href="mailto: hansonliao982@gmail.com?subject = Feedback&body = Message"
          target="_blank"
          rel="noopener noreferrer"
        >
          <Reveal5>
            <img
              src={email}
              alt=""
              className="absolute w-16 linear  hover:animate-ping"
            />
            <img src={email} alt="" className="w-16 " />
          </Reveal5>
        </a>
      </div>
    </div>
  );
};

export default Contact;
