import React from "react";
import Navbar from "../Navbar/Navbar";
import fire from "../../images/fire.gif";
import guy from "../../images/guy.png";

const BannerIntro = () => {
  return (
    <div className="bg-intro-Image bg-cover h-screen pt-20 md:pt-0">
      <div className="grid md:grid-cols-2 w-screen h-full min-h-lg text-white">
        <div className="flex-1 self-center justify-center text-center md:text-left m-auto ">
          <h1 className=" text-5xl md:text-6xl">Hi, I'm Hanson</h1>
          <h1 className=" text-sm px-6 md:px-0 md:text-md pt-3 pb-6 max-w-sm">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
          </h1>
          <div className="m-auto md:m-0 grid md:grid-cols-2 gap-4 max-w-xs">
            <button className="p-3 px-8 rounded-xl border border-purple-600 bg-purple-600 hover:bg-purple-500 ease-in duration-100 font-mono ">
              ABOUT ME
            </button>
            <button className="p-3 px-8 rounded-xl border  hover:bg-slate-800 ease-in duration-100 font-mono ">
              PORTFOLIO
            </button>
          </div>
        </div>
        <div className=" flex self-end justify-center mb-10 min-w-20 mt-5 mb-20 md:mt-0">
          <div className="flex items-end ">
            <img src={fire} alt="" className="h-40  " />
            <img src={guy} alt="" className=" h-60 " />
          </div>
        </div>
      </div>
    </div>
  );
};

export default BannerIntro;
