import React, { useEffect, useState } from "react";
import { RevealBox } from "../utils/RevealPort";

const Project = ({ post }) => {
  const [hover, setHover] = useState(false);
  const [isDesktop, setDesktop] = useState(false);

  useEffect(() => {
    if (window.innerWidth > 544) {
      setDesktop(true);
    } else {
      setDesktop(false);
    }

    const updateMedia = () => {
      if (window.innerWidth > 544) {
        setDesktop(true);
      } else {
        setDesktop(false);
      }
    };
    window.addEventListener("resize", updateMedia);
    return () => window.removeEventListener("resize", updateMedia);
  }, []);

  const handleMouseEnter = () => {
    setHover(true);
  };
  const handleMouseLeave = () => {
    setHover(false);
  };

  return (
    <a
      href="##"
      className=" ease-in duration-100 "
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <RevealBox>
        <div className=" backdrop-blur-lg flex flex-col ease-in duration-150">
          {hover && isDesktop && (
            <div className="absolute flex flex-col ease-in duration-150 ">
              <a href="###" className=" bg-white text-black ">
                GITHUB
              </a>
              <a href="####" className=" bg-white text-black ">
                LEARN MORE
              </a>
            </div>
          )}

          <img
            src={post.image}
            alt=""
            style={{
              width: "auto",
              height: "15rem",
              objectFit: "cover",
              borderRadius: "10px 10px 0 0",
            }}
          />
          {/* ADD HOVER ELEMENT HERE */}

          <div
            className="p-3  bg-slate-400 rounded-b-lg"
            style={{ minHeight: "6rem" }}
          >
            <h3 className="text-xl "> {post.name} </h3>
            <p
              className={`text-sm transition-all ${
                hover && isDesktop ? "hidden" : "block"
              } ease-in duration-150 mb-5`}
            >
              {post.desc}
            </p>
            <div className="flex flex-row transition-all duration-150 w-fit overflow-hidden">
              {hover &&
                isDesktop &&
                post.skills.map((skill) => (
                  <img src={skill} alt="" className="w-fit h-8 my-1" />
                ))}
            </div>
          </div>
        </div>
      </RevealBox>
    </a>
  );
};

export default Project;
