import React from "react";
import { Reveal, Reveal2 } from "../utils/Reveal";
import { RevealBox, RevealTitle } from "../utils/RevealPort";
import sociopedia from "../../media/sociopedia.png";
import javascript from "../../media/javascript.png";
import mongodb from "../../media/mongodb.png";
import html from "../../media/html.png";
import css from "../../media/css.png";
import react from "../../media/react.png";
import node from "../../media/node.png";
import mui from "../../media/mui.png";
import express from "../../media/express.png";
import redux from "../../media/redux.png";
import { useState } from "react";
import { CSSTransition } from "react-transition-group";
import "./style.css";

const data = [
  {
    name: "Sociopedia: A social media app for losers",
    image: sociopedia,
    desc: "A place for pathetic lowlifes to congregate and sob",
    skills: [javascript, mongodb, html, css, react, mui, express, node, redux],
  },
  {
    name: "Sociopedia: A social media app for losers",
    image: sociopedia,
    desc: "A place for pathetic lowlifes to congregate and sob",
    skills: [javascript, mongodb, html, css, react, mui, express, node, redux],
  },
];

const Partfolio = () => {
  const [hover, setHover] = useState(false);

  const handleMouseEnter = () => {
    setHover(true);
  };
  const handleMouseLeave = () => {
    setHover(false);
  };

  return (
    <div id="portfolio" className="">
      <RevealTitle>
        <h2 className="mx-10 md:mx-20 text-2xl mr-80 w-2/6 text-left border-b border-white leading-4 mb-10 h-2">
          <span className="bg-moonlight pr-4">WORK</span>
        </h2>
      </RevealTitle>
      <div className="grid md:grid-cols-2 mx-5 md:mx-20 pb-20 gap-4">
        {data.map((post) => (
          <a
            href="##"
            className=" ease-in duration-75 hover:scale-105 "
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            <RevealBox>
              <div className="w-full h-80  bg-slate-400  backdrop-blur-lg rounded-lg flex flex-col">
                <div className="w-fit">
                  <img
                    src={post.image}
                    alt=""
                    className="w-fit h-fit object-contain rounded-t-lg "
                  />
                  {/* ADD HOVER ELEMENT HERE */}
                </div>
                <div className="p-3">
                  <h3 className="text-xl "> {post.name} </h3>
                  <p
                    className={`text-sm transition-all ${
                      hover ? "hidden" : "block"
                    } ease-in duration-150 `}
                  >
                    {post.desc}
                  </p>
                  <div className="flex flex-row transition-all duration-150 ">
                    {hover &&
                      post.skills.map((skill) => (
                        <img src={skill} alt="" className="w-auto h-10" />
                      ))}
                  </div>
                </div>
              </div>
            </RevealBox>
          </a>
        ))}
      </div>
    </div>
  );
};

export default Partfolio;
