import React from "react";
import { useState } from "react";
import { RevealBox } from "../utils/RevealPort";

const Project = ({ post }) => {
  const [hover, setHover] = useState(false);

  const handleMouseEnter = () => {
    setHover(true);
  };
  const handleMouseLeave = () => {
    setHover(false);
  };

  return (
    <div>
      <a
        href="##"
        className=" ease-in duration-100 hover:scale-105 "
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      >
        <RevealBox>
          <div className="w-100 h-full  bg-slate-400  backdrop-blur-lg rounded-lg flex flex-col">
            <div className="w-100">
              <img
                src={post.image}
                alt=""
                className="w-100 h-fit object-contain rounded-t-lg "
              />
              {/* ADD HOVER ELEMENT HERE */}
            </div>
            <div className="p-3">
              <h3 className="text-xl "> {post.name} </h3>
              <p
                className={`text-sm transition-all h-10 ${
                  hover ? "hidden" : "block"
                } ease-in duration-150 `}
              >
                {post.desc}
              </p>
              <div className="flex flex-row transition-all duration-150 w-fit overflow-hidden">
                {hover &&
                  post.skills.map((skill) => (
                    <img src={skill} alt="" className="w-auto h-10" />
                  ))}
              </div>
            </div>
          </div>
        </RevealBox>
      </a>
    </div>
  );
};

export default Project;
