import React from "react";
import { Reveal, Reveal2 } from "../utils/Reveal";
import { RevealBox, RevealTitle } from "../utils/RevealPort";
import sociopedia from "../../media/sociopedia.png";
import { paste } from "@testing-library/user-event/dist/paste";
import { useState } from "react";

const data = [
  {
    name: "Sociopedia: A social media app for losers",
    image: sociopedia,
    desc: "A place for pathetic lowlifes to congregate and sob",
  },
];

const Partfolio = () => {
  const [hover, setHover] = useState(false);

  const handleMouseEnter = () => {
    setHover(true);
  };
  const handleMouseLeave = () => {
    setHover(false);
  };

  return (
    <div id="portfolio">
      <RevealTitle>
        <h2 className="mx-10 md:mx-20 text-2xl mr-80 w-2/6 text-left border-b border-white leading-4 mb-10 h-2">
          <span className="bg-moonlight pr-4">WORK</span>
        </h2>
      </RevealTitle>
      <div className="grid md:grid-cols-2 mx-5 md:mx-20 pb-20 gap-4">
        {data.map((post) => (
          <a
            href="##"
            className="w-full h-80 backdrop-blur-lg bg-slate-500 rounded-lg"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            <RevealBox>
              <div
                className={`absolute w-full h-80  bg-slate-400  backdrop-blur-lg rounded-lg p-4 ${
                  hover ? "" : "hidden"
                } `}
              >
                hello
              </div>
              <div className={` ${hover ? "hidden" : ""} `}>
                <img
                  src={post.image}
                  alt="sociopedia image"
                  className="rounded-t-lg"
                />
              </div>
              <div className=" backdrop-blur-lg rounded-b-lg p-4 h-auto">
                <h3 className=" text-xl ">{post.name}</h3>
                <p className=" text-sm ">{post.desc} </p>
              </div>
            </RevealBox>
          </a>
        ))}
      </div>
    </div>
  );
};

export default Partfolio;
