import CloseIcon from "@mui/icons-material/Close";
import React, { useContext, useState } from "react";
import { userContext } from "../../Context/userContext";
import MenuIcon from "@mui/icons-material/Menu";
import { useEffect } from "react";

const Navbar = () => {
  const { isDesktop, isLoading } = useContext(userContext);
  const [isNavbarOpened, setIsNavbarOpened] = useState(false);
  const [showNav, setShowNav] = useState(false);

  const controlNavbar = () => {
    if (window.scrollY > 100) {
      setShowNav(false);
    } else {
      setShowNav(true);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", controlNavbar);

    return () => {
      window.removeEventListener("scroll", controlNavbar);
    };
  }, []);

  if (showNav) {
    if (isDesktop) {
      return (
        <>
          <div
            class={`w-screen flex justify-end pe-10 sticky top-0 h-0 text-sm uppercase font-mono ${
              isLoading ? "z-9" : "z-20"
            } `}
          >
            <a
              href="#home"
              className="p-5 hover:underline hover:underline-offset-4"
            >
              Home
            </a>
            <a
              href="#about"
              className="p-5 hover:underline hover:underline-offset-4"
            >
              About
            </a>
            <a
              href="#portfolio"
              className="p-5 hover:underline hover:underline-offset-4"
            >
              Portfolio
            </a>
            <a
              href="#design"
              className="p-5 hover:underline hover:underline-offset-4"
            >
              Design
            </a>
            <a
              href="#contact"
              className="p-5 hover:underline hover:underline-offset-4"
            >
              Contact
            </a>
          </div>
        </>
      );
    } else {
      return (
        <div class={`w-screen sticky h-0 ${!isLoading && "z-20"}  top-0 `}>
          <div
            className={`${isNavbarOpened && "h-screen"}`}
            onClick={() => setIsNavbarOpened(!isNavbarOpened)}
          >
            <div
              class={`flex flex-col text-md text-white shadow-lg ${
                isNavbarOpened && " bg-slate-700/70 "
              } ease-in-out transition-all duration-500`}
            >
              <div class="p-5 text-md flex justify-end backdrop-blur-md z-30">
                <MenuIcon onClick={() => setIsNavbarOpened(!isNavbarOpened)} />
              </div>
              <div
                className={`${
                  isNavbarOpened ? "pt-[0px]" : "mt-[-600px]"
                } backdrop-blur-md flex flex-col w-full uppercase font-mono shadow-lg ease-in-out transition-all duration-500 `}
              >
                <a href="#home" class="p-7">
                  Home
                </a>
                <a href="#about" class="p-7">
                  About
                </a>
                <a href="#portfolio" class="p-7">
                  Portfolio
                </a>
                <a href="#design" class="p-7">
                  Design
                </a>
                <a href="#contact" class="p-7">
                  Contact
                </a>
              </div>
            </div>
          </div>
        </div>
      );
    }
  }
};

export default Navbar;
